function myReadXmlNodes(xmlfile){
	//读取这个xml文件，生成所有xml文件中的控件
	var source=[];
	$.ajax({
		url:sys.path+"/XMLFileReader", type:"post", dataType:'text', async:false, 
		data: { xmlfilename:xmlfile	},
		success: function(data){
			eval("source="+data);
		}
	});
	pservice={};
	pservice.forms=[];
	pservice.tabs=[];
	pservice.fieldset=[];
	for (i=0; i<source.length; i++){
		node=source[i];
		type=node.nodetype;
		if (type=='service') pservice.master=node;
		else if (type=='table') pservice=extend(node, pservice); //合并属性
		else if (type=='form') pservice.forms.push(source[i]);
		else if (type=='tab' || type=='tabs') pservice.tabs.push(source[i]);
		else if (type=='fieldset') pservice.fieldset.push(source[i]);
	}
	for (i=0; i<pservice.forms.length; i++){
		//myShowPanel(pservice.forms[i].parent, true);
		myForm(pservice.forms[i]);
	}	
	for (i=0; i<pservice.tabs.length; i++){
		//myShowPanel(pservice.tabs[i].parent, true);
		myTabs(pservice.tabs[i]);
	}	
	for (i=0; i<pservice.fieldset.length; i++){
		//myShowPanel(pservice.fieldset[i].parent, true);
		myFieldset(pservice.fieldset[i]);
	}	
	//生成其他控件
	pservice.formfields='';
	for (i=0; i<source.length; i++){
		node=source[i];
		type=node.nodetype;
		if (type=='hiddenfield'){  //表单控件
			source[i].parent='body';
			myHiddenFieldst(node.id);
		}else if (type=='textfield'){
			myTextField(node);
			pservice.keydownfields+=';'+node.id;
		}else if (type=='datefield'){
			myDateField(node);
			pservice.keydownfields+=';'+node.id;
		}else if (type=='numberfield'){
			myNumberField(node);
			pservice.keydownfields+=';'+node.id;
		}else if (type=='spinnerfield'){
			mySpinnerField(node);
		}else if (type=='editorfield'){
			myEditorField(node);
		}else if (type=='imagefield'){
			myImageField(node);
		}else if (type=='combofield'){
			myComboField(node);
			pservice.keydownfields+=';'+node.id;
		}else if (type=='dbcombofield'){
			myDBComboField(node);
			pservice.keydownfields+=';'+node.id;
		}else if (type=='checkboxfield'){
			myCheckBoxField(node);
		}else if (type=='timefield'){
			myTimeField(node);
			pservice.keydownfields+=';'+node.id;
		}else if (type=='searchfield'){
			mySearchField(node);
			pservice.keydownfields+=';'+node.id;
		}else if (type=='labelfield'){
			myLabelField(node);
			//console.log(node.id,node.field,node);
		}else if (type=='label'){
			myLabel(node);
			//console.log(node.id,node.field,node);
		}else if (type=='filefield'){
			myFileField(node);
		}else if (type=='line'){
			myLine(node);
			//alert(19);
		}
		if (type.substr(type.length-5, 5)=='field' ){
			pservice.formfields+=';'+node.id;
		}
		if ($('#'+node.parent).length>0){
			//计算父节点的高度,用于高度自动适应
			c1=$('#'+node.parent);
			c2=$('#'+node.id);
			if (c1.attr('yheight')==undefined) c1.attr('yheight', 1*c2.attr('xtop')+1*c2.attr('xheight')+2);
			else c1.attr('yheight', Math.max(c1.attr('yheight'),1*c2.attr('xtop')+1*c2.attr('xheight')+2));
			if (c1.attr('ywidth')==undefined) c1.attr('ywidth', 1*c2.attr('xleft')+1*c2.attr('xwidth')+2);
			else c1.attr('ywidth', Math.max(c1.attr('ywidth'),1*c2.attr('xleft')+1*c2.attr('xwidth')+2));
//			console.log(node.parent,node.id, c1.attr('xheight'),1*c2.attr('xheight'),1*c2.attr('xtop'));
		}
	        //y += o.offsetTop; 
		//
	} //for i=0...next node
	return pservice;	
}
	
